import { useState } from 'react'
const Display = ({ countryList }) => {
  const [newCountryList, setNewCountryList] = useState(countryList)
  const handleClick = (event, name) => {
    event.preventDefault()
    setNewCountryList(newCountryList.filter(country => country.name.common === name))
    showCountry(name)
  }
  const showCountry = (name) => {
    console.log("country showed"
    )
    const country = newCountryList.find(currentCountry => currentCountry.name.common === name)
    const arr = Object.values(country.languages)
    return (
      <div>
        <h1>{country.name.common}</h1>
        <div>
          capital {country.capital}
        </div>
        <div>
          area {country.area}
        </div>
        <div>
          <h3>
            languages:
          </h3>
        </div>
        <div>
          <ul>
            {arr.map(language => <li>{language}</li>)}
          </ul>
        </div>
        <div>
          <img src={country.flags.png}>
          </img>
        </div>
      </div>

    )
  }
  if (newCountryList === null) {
    return null
  }
  else if (newCountryList.length > 10) {
    return (
      <div>
        Too many matches, specify another filter
      </div>
    )
  }
  else if (newCountryList.length === 1) {
    return showCountry(countryList[0].name.common)
  }
  else if (newCountryList.length <= 10) {
    return (
      newCountryList.map(country => <div>{country.name.common}
        <button onClick={(e) => handleClick(e, country.name.common)}>show</button></div>)
    )
  }
  return (
    null
  )
}

export default Display
